(window.webpackJsonp=window.webpackJsonp||[]).push([[60],{340:function(t,n,a){"use strict";a.r(n);var e=a(14),s=Object(e.a)({},(function(){var t=this,n=t._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("h1",{attrs:{id:"所思-时间都去哪儿了"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#所思-时间都去哪儿了"}},[t._v("#")]),t._v(" 所思 - 时间都去哪儿了")]),t._v(" "),n("blockquote",[n("p",[t._v("法国哲学家Paul Janet在1897年提出了人类对时间流逝的心理感受速度的理论。在他的理论中，1年的时间在人的记忆中所占的比重是不一样的。比如，当你1岁的时候，1年就是你生命的全部。当你50岁的时候，1年只是你生命的2%。这样，你的生命越长，1年时间所占的比重就越小。���意味着让一个5岁的小孩为圣诞节等待24天，相当于让一个54岁的的人等待1年。")])]),t._v(" "),n("p",[t._v("最近觉得时间过得特别快，比如一天还没做多少工作，被满满的会议挤掉了一天的日程，被没完没了的用户工单冲掉了零星时间。然后一天过去了，一周过去了，回想这些时间，总有碌碌无为的感觉，只有日益清凉的头顶，和支付宝雷打不动的两三位数。然后想起了上面这个哲学家的理论。\n"),n("img",{attrs:{src:"/yuque/0/2020/png/139763/1594133293446-ab961e25-6ac0-4e47-b585-477663359c9c.png",alt:"image.png"}}),t._v("\n高一的时候写过一篇文章，老师给打了满分，大意是基于这个理论，去讲在加速死亡的人生中我们能怎么样证明自己活过，写了挺多漂亮的句子。如人际的缘分、知识的积累、思考的突破，每一点都是在量变中积累的，不要看不起自己一天的积累，可以写下来。\n每年年终我都会写总结，想想这一年都干了啥，是朝着梦想爬了几个阶梯，还是朝着人性之劣根衰老了一岁。但是随着年纪增长和工作责任的增加，已经越来越难记住这一年到底都做了什么。我们工作会写周报，会写月度总结，会写年度绩效复盘，往往到这个时候都几句话就能讲完，亮点之事往往都是一两天里灵光一闪的小创造，而大部分的小积累往往会被忽略，然后被自己忘记，被世界忘记。这其实挺可惜的，活得太不值了，在复盘的时候也无迹可寻。\n有两条算式挺有意思，分享一下：")]),t._v(" "),n("p",[t._v("1.01^365 = 37.78343433288728\n0.99 ^ 365 = 0.025517964452291125")]),t._v(" "),n("p",[t._v("每天记录些什么，成长些什么；和每天放松一下，软弱一下，分别是什么样的。在一个加速死亡的人生里，不想错过每天的精彩，所以可以每天一些记录不要让日子白过了；不想把一年过成一天，所以每年一些变化不要让日子过重复了。不管是职业、技能、生活方式。")]),t._v(" "),n("p",[t._v("所以 Mark 一下，明天写点职业技术上的事情\n"),n("img",{attrs:{src:"/yuque/0/2020/png/139763/1594133323897-4f806013-b772-4e2d-8116-f93b582fa47e.png",alt:"image.png"}})])])}),[],!1,null,null,null);n.default=s.exports}}]);